import React, { useEffect, useState } from 'react';
import { getBooks, addBook, updateBook, deleteBook } from './services/bookService';
import BookList from './components/BookList';
import BookForm from './components/BookForm';
import OpenAIChatbot from './chatbot/CohereChatbot'; // ✅ updated import
import './App.css';

function App() {
  const [books, setBooks] = useState([]);
  const [currentBook, setCurrentBook] = useState(null);
  const [search, setSearch] = useState("");

  const loadBooks = async () => {
    const res = await getBooks();
    setBooks(res.data);
  };

  useEffect(() => { loadBooks(); }, []);

  const handleSave = async (book) => {
    if (book.id) {
      await updateBook(book.id, book);
    } else {
      await addBook(book);
    }
    setCurrentBook(null);
    loadBooks();
  };

  const handleDelete = async (id) => {
    await deleteBook(id);
    loadBooks();
  };

  const filteredBooks = books.filter(book =>
    book.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <h1>Book Catalog</h1>
      <input
        type="text"
        placeholder="Search by title"
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="search-box"
      />
      <BookForm onSave={handleSave} currentBook={currentBook} />
      <BookList books={filteredBooks} onEdit={setCurrentBook} onDelete={handleDelete} />

      <ChatBotComponent books={books} loadBooks={loadBooks} />

    </div>
  );
}

export default App;
