export default class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  addMessage(message) {
    const botMessage = this.createChatBotMessage(message);
    this.setState((prevState) => ({
      ...prevState,
      messages: [...prevState.messages, botMessage],
    }));
  }

  handleAdd() {
    this.addMessage("To add a book, fill in the form and click Save.");
  }

  handleDelete() {
    this.addMessage("To delete a book, click the Delete button beside the book.");
  }

  handleUpdate() {
    this.addMessage("To update a book, click Edit, modify details, and Save.");
  }

  handleDefault() {
    this.addMessage("I'm not sure what you mean. Try asking how to add, delete, or update a book.");
  }
}
