export default function ActionProvider({ createChatBotMessage, setState }) {
    const handleAdd = () => {
      const message = createChatBotMessage("To add a book, fill in the form above and click Save.");
      setState((prev) => ({ ...prev, messages: [...prev.messages, message] }));
    };

    const handleDelete = () => {
      const message = createChatBotMessage("Click the Delete button next to a book to remove it.");
      setState((prev) => ({ ...prev, messages: [...prev.messages, message] }));
    };

    const handleDefault = () => {
      const message = createChatBotMessage("I'm not sure. Try asking how to add or delete a book.");
      setState((prev) => ({ ...prev, messages: [...prev.messages, message] }));
    };

    // ✅ Just return null — this component is logic-only
    return null;
  }
