import { createChatBotMessage } from 'react-chatbot-kit';

export default function ActionProvider({ createChatBotMessage, setState, children }) {
  const handleAdd = () => setState(prev => ({ ...prev, messages: [...prev.messages, createChatBotMessage("To add a book, fill in the form above and click Save.")] }));
  const handleDelete = () => setState(prev => ({ ...prev, messages: [...prev.messages, createChatBotMessage("Click the Delete button next to a book to remove it.")] }));
  const handleDefault = () => setState(prev => ({ ...prev, messages: [...prev.messages, createChatBotMessage("I'm not sure. Try asking how to add or delete a book.")] }));
  return children({ handleAdd, handleDelete, handleDefault });
}
