export async function callCohere(userMessage) {
    const response = await fetch("https://api.cohere.ai/v1/chat", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${process.env.REACT_APP_COHERE_API_KEY}`,
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        model: "command-r-plus",
        message: userMessage,
        temperature: 0.3,
        chat_history: [],
        connectors: [],
        tools: []
      })
    });

    const result = await response.json();
    console.log("Cohere response:", result);

    try {
      const content = result.text || result.reply;
      return JSON.parse(content);
    } catch (err) {
      throw new Error("Failed to parse Cohere response: " + result.text);
    }
  }
