export default function ActionProvider({ createChatBotMessage, setState }) {
    function handleAdd() {
      const message = createChatBotMessage("To add a book, fill in the form and click Save.");
      setState((prev) => ({
        ...prev,
        messages: [...prev.messages, message],
      }));
    }

    function handleDelete() {
      const message = createChatBotMessage("To delete a book, click the Delete button next to it.");
      setState((prev) => ({
        ...prev,
        messages: [...prev.messages, message],
      }));
    }

    function handleDefault() {
      const message = createChatBotMessage("Sorry, I didn't understand. Try asking how to add or delete a book.");
      setState((prev) => ({
        ...prev,
        messages: [...prev.messages, message],
      }));
    }

    return {
      handleAdd,
      handleDelete,
      handleDefault
    };
  }
